const original = $('Combining Framework Review').first().json
const scores = $json?.output?.component_scores
const summary =$json?.output?.summary
const reasoning = $json?.output?.reasoning
//ICP SCORES
const total_score = 
  (scores?.job_title?.total_score || 0) + 
  (scores?.industry?.total_score || 0) + 
  (scores?.location?.total_score || 0) + 
  (scores?.company_size?.total_score || 0) + 
  (scores?.frameworks?.total_score || 0) + 
  (scores?.intent?.total_score || 0);

let total_score_string = '';

if (total_score >= 50 && total_score <= 62) {
  total_score_string = 'ðŸ”¥ Great Prospect Identified';
} else if (total_score >= 31 && total_score <= 49) {
  total_score_string = 'ðŸ˜€ Good Prospect Identified';
} else if (total_score >= 20 && total_score <= 30) {
  total_score_string = 'â˜¹ï¸ Poor Prospect Identified';
} else {
  total_score_string = 'Website Visitor Identified';
}
// ICP TITLES
let icp_job_title = ''
if(scores?.job_title?.score == 4){
  icp_job_title = "High Authority - " + original?.user_info?.role
}else if(scores?.job_title?.score == 3){
    icp_job_title = "Strong Influence - " + original?.user_info?.role
}else if(scores?.job_title?.score == 2){
    icp_job_title = "Contributing Role - " + original?.user_info?.role
}else{
    icp_job_title = "Limited Influence - " + original?.user_info?.role
