var timestamp = new Date($json.upcoming_meeting_data.meeting_time);
timestamp.setHours(timestamp.getHours() - 5); // Adjusting time to America/New_York
var hours = timestamp.getUTCHours();
var minutes = timestamp.getUTCMinutes();
var suffix = hours >= 12 ? "PM" : "AM";

hours = hours % 12 || 12; // the hour '0' should be '12'
minutes = minutes < 10 ? '0'+minutes : minutes;

var strTime = hours + ':' + minutes + ' ' + suffix;
$json.upcoming_meeting_data.meeting_time = strTime;

return $input.item;

