parameters.jsCode:
const input = $input.item.json
const date = new Date(input.dateString)
let meeting_attendees = ``
input.meeting_attendees.map(attendee =>{
  meeting_attendees += `
   ${attendee.name ?  `Name: ${attendee.name}` + '\n' : ''}${attendee.displayName ? `Display name: ${attendee.displayName}`+'\n': ''}${attendee.email ? `Email: ${attendee.email}`+ '\n':  '\n'}----------`
})
const company_info_exists = input.company_name || input.industry || input.company_size || input.comapny_address
const meeting = `
${input.title} - ${date} meeting
Meeting attendees: ${meeting_attendees}
Meeting summary:
- Keywords: ${input.summary.keywords.length !== 0 ? input.summary.keywords :'not found'} 
- Short summary: ${input.summary.short_summary ?  input.summary.short_summary : 'not found'}
- Outline: ${input.summary.outline ? input.summary.outline : 'not found'}
- Topics discussed: ${input.summary.topics_discussed ? input.summary.topics_discussed : 'not found'}
`
// We didn't include outline since it's empty for every meeting.
$input.item.json.summary = meeting

return $input.item;

