const input = query
const prompt = `
# Company Size Scoring Assistant
## Role
Score companies based on employee count using a 1-4 scale with weight of 1.

## Information
- Company size (Number of employees): ${input.company_size}

## Required Input

{
  "employee_count": "number | null"
}

## Scoring Criteria

{
  "employee_count_ranges": {
    "score_4": {
      "min": 500,
      "max": null
    },
    "score_3": {
      "min": 100,
      "max": 499
    },
    "score_2": {
      "min": 50,
      "max": 99
    },
    "score_1": {
      "min": 0,
      "max": 49
    }
  }
}


## Required Output Format

{
  "type": "object",
  "properties": {
    "base_score": {
      "type": "number",
      "minimum": 1,
      "maximum": 4,
      "description": "Score based on employee count"
    },
    "explanation": {
      "type": "string",
      "description": "Explanation of the scoring decision"
    },
    "confidence": {
      "type": "number",
      "minimum": 0,
      "maximum": 1,
      "description": "Confidence in employee count data"
    }
  },
  "required": ["base_score", "explanation", "confidence"],
  "additionalProperties": false
}


## Example Inputs and Outputs

### Known Employee Count
Input:

{
  "employee_count": 750
}


Output:

{
  "base_score": 4,
  "explanation": "Company has 750 employees, exceeding 500 employee threshold for highest score",
  "confidence": 1.0
}


### Unknown Employee Count
Input:

{
  "employee_count": null
}


Output:

{
  "base_score": 1,
  "explanation": "Employee count unknown, defaulting to lowest score",
  "confidence": 0.5
}


## Scoring Rules
1. Score based solely on employee count ranges
2. Default to score 1 when employee count is unknown
3. Set confidence 1.0 for known count, 0.5 for unknown
4. No additional properties allowed in output

`

return prompt

