const htmlString = $json.content.rendered
const formatDocument = (htmlString) => {

  const removeHtml = (str) => {
    return str
      .replace(/<[^>]*>/g, '') 
      .replace(/\n{3,}/g, '\n\n') 
      .replace(/\s{2,}/g, ' ')
      .trim(); 
  };


  const titleMatch = htmlString.match(/<h1[^>]*>(.*?)<\/h1>/);
  const title = titleMatch ? removeHtml(titleMatch[1]) : '';

  const content = removeHtml(htmlString.replace(/<h1[^>]*>.*?<\/h1>/, ''));

  return {
    title,
    content
  };
};

// Test with the provided HTML string
const result = formatDocument(htmlString);
return result;
