//PROFILE SEARCH URN
let profile_data = null
try{
  profile_data = $('Profile Search')?.first()?.json
}catch(error){

}

const profile_data_urn = profile_data?.urn
if(profile_data_urn){
  structured_object.urn = profile_data_urn
}
// MX DATA
const email_data  = $('Combining Email data')?.first()?.json
if(email_data?.mx_record){
  structured_object.mx_record = email_data.mx_record
}
if(email_data?.mx_provider){
  structured_object.mx_provider = email_data.mx_provider
}
if(email_data?.mx_security_gateway){
  structured_object.mx_security_gateway = email_data.mx_security_gateway
}
if (structured_object?.page_visited_url) {
    const str = structured_object.page_visited_url;

    const matches = str.match(/^https?\:\/\/([^\/?#]+)(?:[\/?#]|$)/i);
    if (matches && matches[1]) {
       structured_object.page_visited_domain = matches[1].toLowerCase();
    } else {
        // Only use fallback if regex doesn't match
        let domain = str.replace(/^(https?:\/\/)?(www\.)?/, '');
        domain = domain.split('@').pop()          // Handle authentication
                     .split(':')[0]               // Remove port
                     .split(/[/?#]/)[0];          // Remove path and params

        // Basic validation
        if (domain && domain.includes('.') && domain.length > 2) {
              structured_object.page_visited_domain = domain.toLowerCase();
        }
    }
}
return {structured_object};

