 $json.user_info.social_urls.linked_in = newUrl
}
if($json.company_info.social_urls.linked_in){
  const newUrl =  ensureHttps($json.company_info.social_urls.linked_in)
  $json.company_info.social_urls.linked_in = newUrl
}
if($json.technical_data.page_visited_url){
$json.technical_data.page_visited_url = ensureHttps($json.technical_data.page_visited_url)
}
