const jobTitleAllowedScores = [0, 3, 6, 9, 12];
const industryAllowedScores = [0, 2, 8];
const locationAllowedScores = [0, 2, 8];
const companySizeAllowedScores = [0, 1, 2, 3, 4];
const frameworksAllowedScores = [0, 3, 6, 9, 12];
const intentAllowedScores = [0, 3, 6, 9, 12, 15, 18];

function validateScores(scores) {
    const { totalScore, componentScores, maxPossibleScore } = scores;

    // Create a copy of the component scores to avoid modifying the original object
    let updatedComponentScores = { ...componentScores };

    // Check and set invalid component scores to 0
    if (!jobTitleAllowedScores.includes(updatedComponentScores.jobTitle)) {
        updatedComponentScores.jobTitle = 0;
    }

    if (!industryAllowedScores.includes(updatedComponentScores.industry)) {
        updatedComponentScores.industry = 0;
    }

    if (!locationAllowedScores.includes(updatedComponentScores.location)) {
        updatedComponentScores.location = 0;
    }

    if (!companySizeAllowedScores.includes(updatedComponentScores.companySize)) {
        updatedComponentScores.companySize = 0;
    }

    if (!frameworksAllowedScores.includes(updatedComponentScores.frameworks)) {
        updatedComponentScores.frameworks = 0;
    }

    if (!intentAllowedScores.includes(updatedComponentScores.intent)) {
        updatedComponentScores.intent = 0;
    }

    // Recalculate the total score after adjustment, excluding 'location' score
    const updatedTotalScore = Object.entries(updatedComponentScores)
        .filter(([key]) => key !== 'location') // Exclude location from total calculation
        .reduce((sum, [, score]) => sum + score, 0);

    // Recalculate the max possible score after adjustment, excluding 'location' score
    const updatedMaxPossibleScore = Object.entries(updatedComponentScores)
        .filter(([key]) => key !== 'location') // Exclude location from max possible score calculation
        .reduce((sum, [key, score]) => {
            const allowedScores = {
                jobTitle: jobTitleAllowedScores,
                industry: industryAllowedScores,
                companySize: companySizeAllowedScores,
                frameworks: frameworksAllowedScores,
                intent: intentAllowedScores
            };
            return sum + Math.max(...allowedScores[key]);
        }, 0);

    // Return the new object with corrected scores, excluding location from total and max score
    return {
        totalScore: updatedTotalScore,
        componentScores: updatedComponentScores,
        maxPossibleScore: updatedMaxPossibleScore
    };
}

const scores = input.overall_score;
const updatedScores = validateScores(scores);
input.overall_score = updatedScores;

function getTier(scores) {
    const { totalScore } = scores;

    if (totalScore >= 44 && totalScore <= 54) {
        return "1";
    } else if (totalScore >= 33 && totalScore <= 43) {
        return "2";
    } else if (totalScore >= 20 && totalScore <= 32) {
        return "3";
    } else {
        return "";  // If the totalScore doesn't fall into any of the ranges
    }
}

function getTierWithEmoji(scores) {
    const { totalScore } = scores;

    if (totalScore >= 44 && totalScore <= 54) {
        return " Tier 1";
    } else if (totalScore >= 33 && totalScore <= 43) {
        return " Tier 2";
    } else if (totalScore >= 20 && totalScore <= 32) {
        return " Tier 3";
    } else {
        return "";  // If the totalScore doesn't fall into any of the ranges
    }
}

const tier = getTier(input.overall_score);
input.total_score_string = tier;
const tier_with_emoji = getTierWithEmoji(input.overall_score);
input.total_score_string_emoji = tier_with_emoji;

original.company_info.icp_score = input;

return original;

function isWithinLastMonth(timestamp) {
  const now = new Date();
  const oneMonthAgo = new Date(now.setMonth(now.getMonth() - 1));

  const timestampDate = new Date(timestamp);

  return timestampDate >= oneMonthAgo;
}


