const original = $('Combining Page Info. Data').first().json

const products = $json?.output?.products || []
const unique_selling_proposition = $json?.output?.unique_selling_proposition || ""
const clients = $json?.output?.clients || []
const testimonials = $json?.output?.testimonials || []
const case_studies = $json?.output?.case_studies || []
const report = $json?.output?.report || ""
let years_in_business = null
if(original.company_info.founded){
  const yearFounded =original.company_info.founded ; // Or "2014" if it's a string

// Get the current year
const currentYear = new Date().getFullYear();

// Convert yearFounded to a number if it's a string
const foundedYear = Number(yearFounded);

// Calculate the years in business
years_in_business  = currentYear - foundedYear;
}



original.company_info.products = products
original.company_info.unique_selling_proposition = unique_selling_proposition
original.company_info.clients = clients
original.company_info.testimonials = testimonials
original.company_info.case_studies = case_studies
original.company_info.report = report
original.company_info.years_in_business = years_in_business





return original;

const structured_object = $json?.output


const fallback_data = $('Combining Company - User - Technical Data').first().json

const transformArraysToFirstItem = (inputData) => {
  // Helper function to check if value should be processed
  const shouldProcessArray = (key, parentKey) => {
    const exclusions = {
      user_info: ['experiences'],
      company_info: ['industries', 'similar_organizations', 'specialities']
    };

    return !(exclusions[parentKey] && exclusions[parentKey].includes(key));
  };

  // Deep clone to avoid modifying original data
  const processObject = (obj, parentKey = '') => {
    const result = {};

    for (const [key, value] of Object.entries(obj)) {
      if (Array.isArray(value) && shouldProcessArray(key, parentKey)) {
        // Transform array to first item if it should be processed
        result[key] = value[0];
      } else if (typeof value === 'object' && value !== null) {
        // Recursively process nested objects
        result[key] = processObject(value, key);
      } else {
        // Keep non-array values as is
        result[key] = value;
      }
    }

    return result;
  };

  // Process the object directly
  return processObject(inputData);
};



if(structured_object){
  return structured_object
}else{
 return transformArraysToFirstItem({
    user_info: fallback_data.user_info,
    company_info:fallback_data.company_info,
    technical_data: fallback_data.technical_data,
    upcoming_meeting_data: fallback_data.upcoming_meeting_data
 })
}

