parameters.jsCode:
function ensureHttps(url) {
    // First remove any existing protocol
    const urlWithoutProtocol = url.replace(/^https?:\/\//, '');

    // Remove query parameters if present
    const urlWithoutQuery = urlWithoutProtocol.split('?')[0];

    // Always add https:// prefix
    return 'https://' + urlWithoutQuery;
}
function extractDomain(url) {
  let domain = url;

  // Remove protocol (http, https, etc.) if present
  const protocolIndex = domain.indexOf("://");
  if (protocolIndex !== -1) {
    domain = domain.substring(protocolIndex + 3);
  }

  // Remove everything after the domain (path, query parameters, etc.)
  const pathIndex = domain.indexOf("/");
  if (pathIndex !== -1) {
    domain = domain.substring(0, pathIndex);
  }

  return domain;
}
