let tasks = $input.all()

// ---------------- EXTRACTING JSON   ------------------
const converted = []
for(t of tasks){
  converted.push(t.json)
}
tasks = converted

// ----------------- FINDING PARENT NAME ------------------
for(task of tasks){
  const parent_task = tasks.find(t => t.id === task.parent_id)
  task.parent_task_name = parent_task ? parent_task.name : null
}


// ----------------- FINDING TASK TYPE --------------------- 
for(t of tasks){
  const task = t
  let task_type_name = null
  let task_type_id = null
  for(field of task.custom_fields){
    if(field.id === 'bb1e9573-2d91-48a9-9b55-0b6aa228b71b'){
      const options = field.type_config.options
      const type = options.filter(option => option.orderindex === field.value);
      if (type.length > 0) {
        task_type_name = type[0].name;
        task_type_id = type[0].id
      }
    }
  }
  task.type_name = task_type_name
  //task.type_id = task_type_id
  delete task.custom_fields
}


// --------------- FINDING TASK TYPE FOR SUBTASKS --------------------
for(t of tasks){
  if(t.parent_id){
    const parent_task = tasks.find(task => task.id == t.parent_id);
    let new_type = 'Not Found'
    if(parent_task){
      new_type = parent_task.type_name
    }else{
      console.log('Parent task not found')
    }
    t.type_name = new_type
  }
}
// ------------------ CONVERTING DATE TO STRING --------------------
for (let t of tasks) {
  let date_string = '';
  if (t.date_closed) {
    const date = new Date(t.date_closed);
    date_string = date.toLocaleDateString('en-US'); 
  }
  t.date_closed_string = date_string;
}
// ---------- REMOVING TO DO TASKS -----------------------
tasks = tasks.filter(task => task.status !== 'to do')
// ---------- FILTERING DONE TASKS THAT CLOSED THIS WEEK ------------
function getTasksClosedThisWeek(tasks) {
    const today = new Date();
    const dayOfWeek = today.getDay(); // 0 (Sunday) to 6 (Saturday)

    // Find last Saturday
    const lastSaturday = new Date(today);
    lastSaturday.setDate(today.getDate() - dayOfWeek - 1 + (dayOfWeek === 6 ? 7 : 0)); // Adjust if today is Saturday

    // Normalize lastSaturday and today to 00:00:00 time for accurate comparison
    lastSaturday.setHours(0, 0, 0, 0);
    today.setHours(23, 59, 59, 999);

    return tasks.filter(task => {
        if (task.date_closed) {
            const closedDate = new Date(parseInt(task.date_closed, 10)); // Convert timestamp to Date
            return closedDate >= lastSaturday && closedDate <= today;
        }
        return true; // Include tasks without a `date_closed`
    });
}
tasks = getTasksClosedThisWeek(tasks)
// -------- GROUPING THEM BY TYPE -------------------
const groupedByType = tasks.reduce((acc, task) => {

  const category = task.type_name || "No Category";

  if (!acc[category]) {
    acc[category] = [];
  }

  acc[category].push(task);
  return acc;
}, {});


const sortedGroupedByType = Object.entries(groupedByType)
  .sort(([keyA], [keyB]) => (keyA === "No Category" ? 1 : keyB === "No Category" ? -1 : 0))
  .reduce((acc, [key, value]) => {
    acc[key] = value;
    return acc;
  }, {});

tasks = sortedGroupedByType;


// ---------- ORDERING THEM BY STATUS --------------------
function sortTasksByStatus(tasks) {
    const statusOrder = { "done": 1, "doing": 2, "in review": 3 };

    const sortedData = {};

    for (const [category, taskList] of Object.entries(tasks)) {
        sortedData[category] = taskList.sort((a, b) => {
            return (statusOrder[a.status] || 4) - (statusOrder[b.status] || 4);
        });
    }

    return sortedData;
}
tasks = sortTasksByStatus(tasks)

// CONVERTING BACK TO FLAT ARRAY
function convertToFlatArray(tasksObj) {
    let flatArray = [];

    for (const [category, tasks] of Object.entries(tasksObj)) {
        tasks.forEach(task => {
            flatArray.push({
                ...task, // Keep all original task properties
                type_name: category // Add category as a new property
            });
        });
    }

    return flatArray;
}

tasks = convertToFlatArray(tasks)
return tasks

