let tasks = []
for(task of $input.all()){
  tasks.push(task.json)
}

tasks = tasks.filter(task => task.status === "done")


function generateQuickChartDataset(tasks) {
    const datasetMap = new Map();
    const labelsSet = new Set();

    tasks.forEach(task => {
        const dateClosed = task.date_closed_string || "No closed date";
        labelsSet.add(dateClosed);

        if (!datasetMap.has(task.type_name)) {
            datasetMap.set(task.type_name, new Map());
        }

        const typeData = datasetMap.get(task.type_name);
        typeData.set(dateClosed, (typeData.get(dateClosed) || 0) + 1);
    });

    const labels = Array.from(labelsSet).sort();

    const datasets = Array.from(datasetMap.entries()).map(([label, data]) => {
        return {
            label,
            data: labels.map(date => data.get(date) || 0)
        };
    });

    return { labels, datasets };
}
const grouped = generateQuickChartDataset(tasks)
return {
    "type":'bar',
    "data": grouped
  }

