            groupedTasks[task.type_name] = {};
        }

        if (!groupedTasks[task.type_name][task.status]) {
            groupedTasks[task.type_name][task.status] = [];
        }

        groupedTasks[task.type_name][task.status].push(task);
    });

    return groupedTasks;
}


