const attendees = []
for (const item of $input.all()) {
  if(item.json.meeting_attendees && item.json.meeting_attendees.length > 0 ){
      for(const attendee of item.json.meeting_attendees ){
        attendees.push(attendee?.email)
      }
  }
}
const uniqueArray = [...new Set(attendees)];

return {uniqueArray}

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
const meetings = $('MockUp Data').all()
const hubspot_contacts = $input.all()

return {meetings, hubspot_contacts}

let meetings = $input.first().json.meetings
let meetings_formatted = []
for(const meeting of meetings){
  meetings_formatted.push(meeting.json)
}
// ---------------------------
let hubspot_contacts = $input.first().json.hubspot_contacts
let hubspot_contacts_formatted = []
for(const contact of hubspot_contacts){
  hubspot_contacts_formatted.push(contact.json)
}
// ---------------------------
for(const meeting of meetings_formatted){
  const hubspot_contacts = []
  const hubspot_companies = []

  if(meeting.meeting_attendees && meeting.meeting_attendees.length >0){
    for(const meeting_attendee of meeting.meeting_attendees){
      const hubspot_contact = hubspot_contacts_formatted.find(contact  => contact.email === meeting_attendee.email)
      if(hubspot_contact?.hubspot_contact_id){
        hubspot_contacts.push(hubspot_contact?.hubspot_contact_id)
      }
      if(hubspot_contact?.hubspot_company_id){
        hubspot_companies.push(hubspot_contact.hubspot_company_id)
      }
    }
  }
  const hubspot_companies_removed_duplicates = [...new Set(hubspot_companies)]

  meeting.hubspot_contacts = hubspot_contacts
  meeting.hubspot_companies = hubspot_companies_removed_duplicates
}



return {meetings_formatted}
